Update WorkflowTask set WorkflowTaskCode = 'WFCO' , Step = 30 where EntityID = (select EntityID from Entity where EntityCode = 'VACHREQ')  and WorkflowTaskCode = 'WFVE'

Update WorkflowTask set NextStep =  30 where EntityID = (select EntityID from Entity where EntityCode = 'VACHREQ')  and WorkflowTaskCode = 'NEW'

Update WorkflowTask set Step =  30 where EntityID = (select EntityID from Entity where EntityCode = 'VACHREQ')  and WorkflowTaskCode = 'REJ'

Update WorkflowTaskRole set Step = 30 where EntityID = (select EntityID from Entity where EntityCode = 'VACHREQ') and Step = 10


